


var information = null;
$("#matricule").change(function(){
    matricule = $("#matricule").val();
    url = `http://localhost/G-School/api/service/reinscription.php?matricule=${matricule}`;
   $.get(url,function (response) {
        data = JSON.parse(response);
        $("#nom").val(data[0].Nom_Etudiant+" "+data[0].Prenom_Etudiant);
        $("#mention").val(data[0].code_mention);
        $("#last_niveau").val(data[0].Id_Niveau);
        now_level= parseInt(data[0].Id_Niveau)+1;
        $("#now_niveau").val(now_level.toString());
        
   })
})

$("#sumbit_data").click(function(){
    data ={
        annee : $("#anne_unniv").val(),
        mention : $("#mention").val(),
        niveau : $("#now_niveau").val(),
        matricule : $("#matricule").val(),
        nom : $("#nom").val(),
    };
    url = "http://localhost/G-School/api/service/reinscription.php";
    $.post(url, JSON.stringify(data), function(res,){
        console.log(res);
       if (res == "err") {
        swal({
            icon: 'warning',
            title: 'Duplication ...',
            text: 'Cette Etudiant est déjà inscrit',
            footer: '<a href="">Why do I have this issue?</a>',
            showCloseButton: true,
          })
          data = null;
       }
       else{
       
        swal("", "Reinscription de l' Etudiant avec success...", "success");
       }
        
      
    });
    
})